import React, { useState } from 'react';

const Toggle = () => {
    //1. enable useState
    const [on, setOn] = useState(false);
    console.log(on);
    const toggle = () => {
        setOn(on = on ? false : true);
    }
    return (
        <div className='toggle' onClick={toggle}>
            toggle {on ? 'On' : 'Off' }
        </div>
    );
};

export default Toggle;
