import './App.css';
import Counter from './components/counter/Counter';
import Toggle from './components/toggle/Toggle';
import { useState } from 'react';


function App() {
  const gifts = [
    'g1', 'g2', 'g3'
  ]

  const [desc, setDesc] = useState(`Pls push button`);
  const handleClick = () => {
    const random = gifts[Math.floor(Math.random() * gifts.length)];
    setDesc(
      random
    )
  }

  return (
    <div className="App">
      <h2>Toggle to test useState() hook</h2>
      <Toggle></Toggle>
      <h2>Counter to learn stale state</h2>
      <Counter></Counter>
      <h2>Random gift</h2>
      <h1>{desc}</h1>
      <button onClick={handleClick}>Get gift</button>
    </div>
  );
}

export default App;
